mapper.select:
select t.* from email t where 1=1 

	and (sendid =#{uid} or toid=#{uid}) and userdeleted=0 and title like '%${content}%' order by userisread,time asc limit #{page},#{size}


	and (sendid=#{uid} or toid=0) and managedelete=0 and title like '%${content}%' order by managerisread,time asc limit #{page},#{size}

mapper.update:
update email set sendid=#{sendid},toid=#{toid},title=#{title},content=#{content},
time=#{time},managerisread=#{managerisread},userisread=#{userisread},userdeleted=#{userdeleted},
managedelete=#{managedelete} where id=#{id}
